import { Component, OnInit } from '@angular/core';
import { AuthService } from './auth.service';
import { Router, RouterOutlet, RouterLink, RouterLinkActive } from '@angular/router';
import { CommonModule } from '@angular/common';
import { WorkoutListComponent } from './workout-list/workout-list.component';
import { WorkoutFormComponent } from './workout-form/workout-form.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  standalone: true,
  imports: [CommonModule, RouterOutlet, RouterLink, RouterLinkActive, WorkoutListComponent, WorkoutFormComponent]
})
export class AppComponent implements OnInit {
  isAuthenticated: boolean = false;

  constructor(public authService: AuthService, private router: Router) { }

  ngOnInit(): void {
    this.authService.isAuthenticated$.subscribe(isAuthenticated => {
      this.isAuthenticated = isAuthenticated;
    });
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav class="navbar">
  <a routerLink="/workouts" class="navbar-brand">Fitness App</a>
  <div class="navbar-links">
    <ng-container *ngIf="authService.isAuthenticated$ | async; else loggedOut">
      <a routerLink="/workouts" routerLinkActive="active">Workouts</a>
      <a routerLink="/add-workout" routerLinkActive="active">Add Workout</a>
      <button (click)="logout()">Logout</button>
    </ng-container>
    <ng-template #loggedOut>
      <a routerLink="/login" routerLinkActive="active">Login</a>
      <a routerLink="/register" routerLinkActive="active">Register</a>
    </ng-template>
  </div>
</nav>

<div class="container">
  <router-outlet></router-outlet>
</div>
