import { Component, EventEmitter, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Workout } from '../workout.model';

@Component({
  selector: 'app-workout-form',
  templateUrl: './workout-form.component.html',
  styleUrls: ['./workout-form.component.css']
})
export class WorkoutFormComponent {
  @Output() workoutAdded = new EventEmitter<Workout>();
  workoutForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.workoutForm = this.fb.group({
      exercise: ['', Validators.required],
      sets: ['', [Validators.required, Validators.min(1)]],
      reps: ['', [Validators.required, Validators.min(1)]],
      duration: ['', [Validators.required, Validators.min(1)]]
    });
  }

  onSubmit(): void {
    if (this.workoutForm.valid) {
      this.workoutAdded.emit(this.workoutForm.value);
      this.workoutForm.reset();
    }
  }
}
<form [formGroup]="workoutForm" (ngSubmit)="onSubmit()">
  <input formControlName="exercise" placeholder="Exercise">
  <input formControlName="sets" placeholder="Sets" type="number">
  <input formControlName="reps" placeholder="Reps" type="number">
  <input formControlName="duration" placeholder="Duration (minutes)" type="number">
  <button type="submit" [disabled]="!workoutForm.valid">Add Workout</button>
</form>
